export class EmpleadoFormComponent {
    constructor(usuarioService, router, activatedRoute, dependenciaService, empleadoService) {
        this.usuarioService = usuarioService;
        this.router = router;
        this.activatedRoute = activatedRoute;
        this.dependenciaService = dependenciaService;
        this.empleadoService = empleadoService;
        this.dependencias = new Array();
        this.dependenciasAgregar = new Array();
        this.accion = "";
        this.dependencia = new Dependencia();
        if (!this.usuarioService.userLoggedIn()) {
            Swal.fire({
                icon: 'error',
                title: 'Acceso denegado',
                text: 'Necesita iniciar sesion!'
            });
            this.router.navigate(['login']);
        }
        else {
            if (this.usuarioService.userPerfil() != "Administrador") {
                Swal.fire({
                    icon: 'error',
                    title: 'Acceso Prohibido...',
                    text: 'No tiene los permisos necesarios!'
                });
                this.router.navigate(['home']);
            }
        }
    }
    altaEmpleado() {
        this.empleado.Dependencias = this.dependenciasAgregar;
        this.empleadoService.createEmpleado(this.empleado).subscribe(result => {
            console.log("1" + this.empleado);
            if (result.status == "1") {
                Swal.fire({
                    position: 'center',
                    icon: 'success',
                    title: 'Empleado creado correctamente!',
                    showConfirmButton: false,
                    timer: 1500
                });
                this.router.navigate(['empleado']);
            }
        }, error => {
            console.log("0" + this.empleado);
            if (error.status == "0") {
                Swal.fire({
                    position: 'center',
                    icon: 'error',
                    title: 'Error al crear empleado',
                    showConfirmButton: false,
                    timer: 1500
                });
            }
        });
    }
    ngOnInit() {
        this.activatedRoute.params.subscribe(params => {
            if (params.id == "0") {
                this.accion = "new";
                this.iniciarEmpleado();
                this.cargarDependencias();
                console.log(this.dependencias);
                ;
            }
            else {
                this.accion = "update";
                this.iniciarEmpleado();
                this.cargarEmpleado(params.id);
            }
        });
    }
    async cargarDependencias() {
        this.dependencias = new Array();
        this.dependenciaService.getDependencias().subscribe(result => {
            var unaDependencia = new Dependencia();
            result.forEach((element) => {
                Object.assign(unaDependencia, element);
                this.dependencias.push(unaDependencia);
                unaDependencia = new Dependencia();
            });
        });
    }
    actualizarEmpleado() {
        this.empleado.Dependencias = this.dependenciasAgregar;
        this.empleadoService.updateEmpleado(this.empleado).subscribe(result => {
            if (result.status == "1") {
                Swal.fire({
                    position: 'center',
                    icon: 'success',
                    title: 'Empleaod Actualizado',
                    showConfirmButton: false,
                    timer: 1500
                });
                this.router.navigate(['empleado']);
            }
        }, error => {
            if (error.status == "0") {
                Swal.fire({
                    position: 'center',
                    icon: 'error',
                    title: 'Error al actualizar empleado',
                    showConfirmButton: false,
                    timer: 1500
                });
            }
        });
    }
    async cargarEmpleado(id) {
        //llamar al servicio de carga de dependencias
        await this.cargarDependencias();
        //llama al servicio para cargar un empleado
        this.empleadoService.getEmpleado(id).subscribe(result => {
            this.empleado = new Empleado();
            Object.assign(this.empleado, result);
            //Dependencias de la reunion
            this.empleado.Dependencias.forEach((element) => {
                this.dependencia = new Dependencia();
                Object.assign(this.dependencia, element);
                this.agregarDependencia(this.dependencia);
                this.dependencia = new Dependencia();
            });
        }, error => {
            console.log(error);
            alert("error");
        });
    }
    cerrar() {
        this.router.navigate(['empleado']);
    }
    iniciarEmpleado() {
        this.empleado = new Empleado();
    }
    //cargar un array auxilar de los participantes a agregar a la reunion
    agregarDependencia(depe) {
        this.dependenciasAgregar.push(depe);
        var index = -1;
        var c = 0;
        this.dependencias.forEach((element) => {
            if (depe._id == element._id) {
                index = c;
            }
            c++;
        });
        this.dependencias.splice(index, 1);
    }
    quitarDependencia(depe) {
        this.dependenciasAgregar.splice(this.dependenciasAgregar.indexOf(depe), 1);
        this.dependencias.push(depe);
    }
}
