export class ReunionFormComponent {
  constructor(oficinaService, empleadoService, recursoService, reunionService, activatedRoute, router, usuarioService, notificacionService) {
    this.oficinaService = oficinaService;
    this.empleadoService = empleadoService;
    this.recursoService = recursoService;
    this.reunionService = reunionService;
    this.activatedRoute = activatedRoute;
    this.router = router;
    this.usuarioService = usuarioService;
    this.notificacionService = notificacionService;
    this.participantes = [];
    this.participantesAgregar = [];
    this.recursosAgregar = [];
    this.oficinas = [];
    this.recursos = [];
    this.reuniones = [];
    this.accion = "";

    if (usuarioService.userLoggedIn() == false) {
      Swal.fire({
        icon: 'error',
        title: 'Acceso denegado',
        text: 'Por favor inicia sesion.'
      });
      router.navigate(['login']);
    }

    this.participantesAgregar = new Array();
    this.participantes = new Array();
    this.reunion = new Reunion();
    this.reunionAux = new Reunion();
    this.oficinas = new Array();
    this.recursos = new Array();
    this.participante = new Empleado();
    this.oficina = new Oficina();
    this.recurso = new Recurso();
    this.recursosAgregar = new Array();
    this.reuniones = new Array();
  }

  altaReunion() {
    this.reunion.participantes = this.participantesAgregar;
    this.reunion.recursos = this.recursosAgregar;
    this.reunion.estadoReunion = "pendiente";
    ;

    if (this.comprobarParticipante() == false) {
      Swal.fire({
        position: 'center',
        icon: 'error',
        title: 'Colision de participantes!',
        showConfirmButton: false,
        timer: 1500
      });
    } else if (this.comprobarRecursos() == false) {
      Swal.fire({
        position: 'center',
        icon: 'error',
        title: 'Colision de recursos!',
        showConfirmButton: false,
        timer: 1500
      });
    } else if (this.comprobarOficinas() == false) {
      Swal.fire({
        position: 'center',
        icon: 'error',
        title: 'Colision de oficina!',
        showConfirmButton: false,
        timer: 1500
      });
    } else if (this.reunion.participantes.length == 0) {
      Swal.fire({
        position: 'center',
        icon: 'error',
        title: 'No seleccionaste ningun participante',
        showConfirmButton: false,
        timer: 1500
      });
    } else {
      this.reunionService.addReunion(this.reunion).subscribe(result => {
        console.log(result);
        Swal.fire({
          position: 'center',
          icon: 'success',
          title: 'Reunion creada correctamente!',
          showConfirmButton: false,
          timer: 1800
        });
        var notificacion;
        this.reunion.participantes.forEach(element => {
          notificacion = new Notificacion();
          notificacion.leido = false;
          notificacion.empleado = element;
          this.reunion._id = result._id;
          notificacion.reunion = this.reunion;
          this.notificacionService.createNotificacion(notificacion).subscribe(result => {
            console.log(result);
          }, error => {
            console.log(error);
          });
        });
        let asunto = "Reunion Empresaurios";
        let mensaje = "Hola a Todos/as, se le informa por medio de este mail que el dia " + this.reunion.fecha + " hay una reunion de '" + this.reunion.titulo + "' en el horario de " + this.reunion.horaInicio + " a " + this.reunion.horaFin + ". Gracias por su atencion Empresaurios.";

        for (let i = 0; i < this.reunion.participantes.length; i++) {
          this.reunionService.enviarCorreo(asunto, this.reunion.participantes[i].Email, mensaje).subscribe(result => {
            console.log(result);
          }, error => {
            console.log(error);
          });
        }

        ;
        this.router.navigate(['tablaReunion']);
      }, error => {
        console.log(error);
        Swal.fire({
          position: 'center',
          icon: 'error',
          title: 'No pudo crearse la reunion!',
          showConfirmButton: false,
          timer: 1800
        });
      });
    }
  } //comprobar colision de participantes 2-i.


  comprobarParticipante() {
    var retornar = true;
    this.reuniones.forEach(element => {
      element.participantes.forEach(elementt => {
        this.reunion.participantes.forEach(elemnttt => {
          if (element._id != this.reunion._id) {
            if (elementt._id == elemnttt._id) {
              if (element.fecha == this.reunion.fecha) {
                if ((this.convertirHora(this.reunion.horaInicio).getHours() > this.convertirHora(element.horaInicio).getHours() || this.convertirHora(this.reunion.horaInicio).getHours() == this.convertirHora(element.horaInicio).getHours() && this.convertirHora(this.reunion.horaInicio).getMinutes() >= this.convertirHora(element.horaInicio).getMinutes()
