import Chart from 'chart.js/auto';
import { Empleado } from 'src/app/models/empleado';
import { Oficina } from 'src/app/models/oficina';
import { Reunion } from 'src/app/models/reunion';
import Swal from 'sweetalert2';
import 'sweetalert2/src/sweetalert2.scss';
import * as i0 from "@angular/core";
import * as i1 from "src/app/service/reunion.service";
import * as i2 from "src/app/service/empleado.service";
import * as i3 from "src/app/service/oficina.service";
import * as i4 from "src/app/service/usuario.service";
import * as i5 from "@angular/router";
import * as i6 from "../layout/header/header.component";
import * as i7 from "@angular/forms";
export class EstadisticasComponent {
    constructor(reunionService, empleadoService, oficinaService, usuarioService, router) {
        this.reunionService = reunionService;
        this.empleadoService = empleadoService;
        this.oficinaService = oficinaService;
        this.usuarioService = usuarioService;
        this.router = router;
        this.oficinas = [];
        this.reuniones = [];
        this.participantes = [];
        this.mostrar = [];
        this.mostrarLabel = [];
        if (usuarioService.userLoggedIn() == false) {
            Swal.fire({
                icon: 'error',
                title: 'Acceso denegado',
                text: 'Por favor inicia sesion.',
            });
            router.navigate(['login']);
        }
        this.mostrar = new Array();
        this.mostrarLabel = new Array();
        this.reuniones = new Array();
        this.reunion = new Reunion;
        this.participantes = new Array();
        this.participante = new Empleado();
        this.oficinas = new Array();
        this.oficina = new Oficina();
        this.dateStart = new Date();
        this.dateEnd = new Date();
        this.getReuniones();
        this.getParticipantes();
        this.getOficinas();
    }
    ngOnInit() {
    }
    /*
    Dibujar la estadistica en Barra
    */
    charBarra(datos, mostrar) {
        if (this.chartBar) {
            this.chartBar.destroy();
        }
        this.chartBar = new Chart("chartBar", {
            type: 'bar',
            data: {
                labels: mostrar,
                datasets: [{
                        label: 'participo en reuniones',
                        data: datos,
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.2)',
                            'rgba(54, 162, 235, 0.2)',
                            'rgba(255, 206, 86, 0.2)',
                            'rgba(75, 192, 192, 0.2)',
                            'rgba(153, 102, 255, 0.2)',
                            'rgba(255, 159, 64, 0.2)'
                        ],
                        borderColor: [
                            'rgba(255, 99, 132, 1)',
                            'rgba(54, 162, 235, 1)',
                            'rgba(255, 206, 86, 1)',
                            'rgba(75, 192, 192, 1)',
                            'rgba(153, 102, 255, 1)',
                            'rgba(255, 159, 64, 1)'
                        ],
                        borderWidth: 1
                    }]
            }
        });
    }
    /*
     Dibujar la estadistica en torta
    */
    charTorta(datos, mostrar) {
        if (this.chartPie) {
            this.chartPie.destroy();
        }
        this.chartPie = new Chart("chartPie", {
            type: 'pie',
            data: {
                labels: mostrar,
                datasets: [{
                        label: 'participo en reuniones',
                        data: datos,
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.2)',
                            'rgba(54, 162, 235, 0.2)',
                            'rgba(255, 206, 86, 0.2)',
                            'rgba(75, 192, 192, 0.2)',
                            'rgba(153, 102, 255, 0.2)',
                            'rgba(255, 159, 64, 0.2)'
                        ],
                        hoverOffset: 4
                    }]
            }
        });
    }
    async getOficinas() {
        this.oficinas = new Array();
        this.oficinaService.getOficinas().subscribe(result => {
            result.forEach((element) => {
                this.oficina = new Oficina();
                Object.assign(this.oficina, element);
                this.oficinas.push(this.oficina);
            });
        }, error => {
            console.log(error);
        });
    }
    async getReuniones() {
        this.reuniones = new Array();
        this.reunionService.gerReuniones().subscribe(result => {
            result.forEach((element) => {
                this.reunion = new Reunion();
                Object.assign(this.reunion, element);
                this.reuniones.push(this.reunion);
            });
        }, error => {
            console.log(error);
        });
    }
    async getParticipantes() {
        this.participantes = new Array();
        this.empleadoService.getEmpleados().subscribe(result => {
            result.forEach((element) => {
                this.participante = new Empleado();
                Object.assign(this.participante, element);
                this.participantes.push(this.participante);
            });
        }, error => {
            console.log(error);
        });
    }
    participantesChar() {
        this.mostrar = new Array();
        this.mostrarLabel = new Array();
        var index = 0;
        var c = 0;
        var b = 0;
        this.participantes.forEach((element) => {
            this.mostrar[b] = 0;
            this.mostrarLabel[b] = element.Apellido + " " + element.Nombre;
            b++;
        });
        this.reuniones.forEach((element) => {
            element.participantes.forEach((elementt) => {
                this.participantes.forEach((elementtt) => {
                    if (elementtt._id == elementt._id) {
                        c++;
                        this.mostrar[index] = this.mostrar[index] + c;
                    }
                    index++;
                });
                c = 0;
                index = 0;
            });
        });
        this.charBarra(this.mostrar, this.mostrarLabel);
        this.charTorta(this.mostrar, this.mostrarLabel);
    }
    ;
    oficinasChar() {
        this.mostrar = new Array();
        this.mostrarLabel = new Array();
        var index = 0;
        var c = 0;
        var b = 0;
        this.oficinas.forEach((element) => {
            this.mostrar[b] = 0;
            this.mostrarLabel[b] = "oficina nro: " + element.numero;
            b++;
        });
        this.reuniones.forEach((element) => {
            this.oficinas.forEach((elementt) => {
                if (element.oficina._id == elementt._id) {
                    c++;
                    this.mostrar[index] = this.mostrar[index] + c;
                }
                index++;
            });
            c = 0;
            index = 0;
        });
        this.charBarra(this.mostrar, this.mostrarLabel);
        this.charTorta(this.mostrar, this.mostrarLabel);
    }
    tipoReunionChar() {
        var arr;
        arr = ["Urgente", "Alta", "Normal"];
        this.mostrar = [0, 0, 0];
        this.mostrarLabel = ["Urgente: ", "Alta: ", "Normal: "];
        var index = 0;
        var c = 0;
